function getMonthsInOrder(start) {
  // Define an array of months in order
  let order = [
    'January', 'February', 'March', 'April', 'May', 'June', 'July', 'August', 'September', 'October', 'November', 'December'
  ];
  // Find the index of the start month
  const startIndex = order.indexOf(start);
  // Use slice to create a new array starting from the start month
  const orderedItems = order.slice(startIndex).concat(order.slice(0, startIndex));
  return orderedItems;
}
function semesters() {
  return ['Fall', 'Spring', 'Summer'];
}
function getSemesterLookup() {
  const lookup = {};
  for (const semester of semesters()) {
    lookup[semester.toLowerCase()] = semester;
  }
  return lookup;
}
function getSemester(lookup, string) {
  let lowerString = string.toLowerCase();

  // Check if the formatted string starts with any semester name
  for (const semester of Object.values(lookup)) {
   if (lowerString.startsWith(semester.toLowerCase())) {
    return semester;
   }
  }
  return '';
 }
function getSemestersInOrder(start) {
  // Define an array of months in order
  let order = semesters();
  // Find the index of the start month
  const startIndex = order.indexOf(start);
  // Use slice to create a new array starting from the start month
  const orderedItems = order.slice(startIndex).concat(order.slice(0, startIndex));
  return orderedItems;
}
// Filter Semester
function filterSemester(event, el) {
  event.preventDefault();
  let button = event.target;
  let activeSemester = button.value;
  if (button.classList.contains('active')) {
    button.classList.remove('active');
    activeSemester = '';
  } else {
    let activeButton = document.querySelector('#academic-calendar-filters .button-group .active');
    if (activeButton) {
      activeButton.classList.remove('active');
    }
    button.classList.add('active');
  }
  // Unfilter before filtering again
  let activeItems = document.querySelectorAll('#academic-calendar-events .show');
  academicCalendarEl.classList.remove('filtered');
  Array.prototype.forEach.call(activeItems, (node, i) => {
    node.classList.remove('show', 'only-past-semester-events');
  });
  if (activeSemester) {
    let months = document.querySelectorAll('#academic-calendar-events .event-month');
    Array.prototype.forEach.call(months, (node, i) => {
      if (node.classList.contains(activeSemester)) {
        node.classList.add('show');
        let nodeEvents = node.querySelectorAll('.event-item');
