          node.classList.add('only-past-semester-events');
        }
      }
    });
    academicCalendarEl.classList.add('filtered');
  }
  return;
}
// Fetch XML File
function fetchXMLFile(url) {
  return new Promise((resolve, reject) => {
    fetch(url)
      .then(response => {
        if (!response.ok) {
          throw new Error(`Network response was not ok: ${response.status}`);
        }
        return response.text();
      })
      .then(xmlString => {
        var parser = new DOMParser();
        var xmlDoc = parser.parseFromString(xmlString, 'application/xml');
        resolve(xmlDoc);
      })
      .catch(error => {
        reject(new Error('Failed to fetch the XML file.'));
      });
  });
}
